(()=>{const e=document.body,t=document.getElementById("mode"),n=t=>{t==="dark"?(localStorage.setItem("theme","light"),e.removeAttribute("data-theme")):t==="light"?(localStorage.setItem("theme","dark"),e.setAttribute("data-theme","dark")):initTheme(t)};t.addEventListener("click",()=>n(localStorage.getItem("theme")));const s=document.getElementById("menu-trigger");s.addEventListener("change",function(){const e=document.querySelector(".wrapper");this.checked?e.classList.add("blurry"):e.classList.remove("blurry")})})();const gttButton=document.getElementById("totop");window.onscroll=()=>{document.body.scrollTop>300||document.documentElement.scrollTop>300?(gttButton.style.visibility="visible",gttButton.style.opacity="1"):(gttButton.style.visibility="hidden",gttButton.style.opacity="0")},gttButton.addEventListener("click",()=>{window.scrollTo({top:0,behavior:"smooth"})})